fetch("https://www.tagesschau.de/export/podcast/hi/tagesschau-in-100-sekunden/").then(res=>res.text()).then(data=>{const parser=new DOMParser,xmlDoc=parser.parseFromString(data,"text/xml"),audioUrl=xmlDoc.querySelector("enclosure").getAttribute("url"),pubDate=xmlDoc.querySelector("item pubDate").innerHTML,dateObj=new Date(pubDate),currentHours=dateObj.getHours(),currentMinutes=dateObj.getMinutes(),formatHours=currentHours>10?currentHours:"0"+currentHours,formatMinutes=currentMinutes>10?currentMinutes:"0"+currentMinutes,pubTime=formatHours+":"+formatMinutes;function skipIntro(x){document.querySelector("audio").paused||document.querySelector("audio").pause(),x.checked?document.querySelector("audio").currentTime=13:document.querySelector("audio").currentTime=0}function updateSpeedStatus(){document.querySelector(".speed-status").innerHTML=document.querySelector(".speed").value}document.querySelector("section").innerHTML=`<p>Beitrag von: ${pubTime} Uhr</p>\n      <audio controls>\n      <source src="${audioUrl}" type="audio/mpeg">Your browser does not support the audio tag.\n      </audio>\n      <p>Geschwindigkeit:<br>\n      <div class="speed-wrapper">\n      <input type="range" class="speed" value="2.2" min="1" max="10" step="0.1"> <span class="speed-status"></span>\n      </div>\n      </p>\n      <p><label>\n      <input type="checkbox" checked> Intro Ã¼berspringen</label></p>\n      <p><a href="${audioUrl}">Quelle</a></p>`,document.querySelector("audio").playbackRate=document.querySelector(".speed").value,document.querySelector(".speed").addEventListener("change",()=>{document.querySelector("audio").playbackRate=document.querySelector(".speed").value,updateSpeedStatus()}),document.querySelector("input[type=checkbox]").addEventListener("change",(function(){skipIntro(this)})),document.querySelector("audio").currentTime=13,updateSpeedStatus()});