fetch("https://www.tagesschau.de/export/podcast/hi/tagesschau-in-100-sekunden/").then(res=>res.text()).then(data=>{const parser=new DOMParser,xmlDoc=parser.parseFromString(data,"text/xml"),audioUrl=xmlDoc.querySelector("enclosure").getAttribute("url"),pubDate=xmlDoc.querySelector("item pubDate").innerHTML,dateObj=new Date(pubDate),currentHours=dateObj.getHours(),currentMinutes=dateObj.getMinutes(),formatHours=currentHours>10?currentHours:"0"+currentHours,formatMinutes=currentMinutes>10?currentMinutes:"0"+currentMinutes,pubTime=formatHours+":"+formatMinutes;function skipIntro(x){document.querySelector("audio").paused||document.querySelector("audio").pause(),x.checked?document.querySelector("audio").currentTime=10:document.querySelector("audio").currentTime=0}document.querySelector("section").innerHTML=`<p>Beitrag von: ${pubTime} Uhr</p>\n      <audio controls>\n      <source src="${audioUrl}" type="audio/mpeg">Your browser does not support the audio tag.\n      </audio>\n      <p>Geschwindigkeit:<br>\n      <button class="speed-btn1">1x</button> <button class="speed-btn2">2x</button> <button class="speed-btn3">2.5x</button>\n      </p>\n      <p><label>\n      <input type="checkbox" checked> Intro Ã¼berspringen</label></p>\n      <p><a href="${audioUrl}">Quelle</a></p>`,document.querySelector("audio").playbackRate=2,document.querySelector(".speed-btn1").addEventListener("click",()=>document.querySelector("audio").playbackRate=1),document.querySelector(".speed-btn2").addEventListener("click",()=>document.querySelector("audio").playbackRate=2),document.querySelector(".speed-btn3").addEventListener("click",()=>document.querySelector("audio").playbackRate=2.5),document.querySelector("input[type=checkbox]").addEventListener("change",(function(){skipIntro(this)})),document.querySelector("audio").currentTime=10});