let langChoice="";function langSelect(x){langChoice=x.value}function speak(){if(document.querySelector(".tts-input").value){const msg=new SpeechSynthesisUtterance;msg.text=document.querySelector(".tts-input").value,msg.lang=langChoice,msg.rate=document.querySelector(".rate").value,speechSynthesis.speak(msg)}else alert("Please enter text first.")}function showVal(){document.querySelector(".rate-value").innerHTML=document.querySelector(".rate").value}function lengthElemHider(){document.querySelector(".tts-input").value.length>3999?document.querySelector(".shorten-warpper").classList.contains("hidden")&&document.querySelector(".shorten-warpper").classList.remove("hidden"):document.querySelector(".shorten-warpper").classList.contains("hidden")||document.querySelector(".shorten-warpper").classList.add("hidden")}function updateLengthValue(){document.querySelector(".input-text-length").innerHTML=document.querySelector(".tts-input").value.length}function pasteText(){try{navigator.clipboard.readText().then(e=>{document.querySelector(".tts-input").value=e}).catch(error=>alert(error))}catch(e){alert(e),document.querySelector(".btn-paste").setAttribute("disabled","")}}document.querySelectorAll("input[type=radio]").forEach(e=>{e.checked&&(langChoice=e.value)}),showVal(),document.querySelector(".btn-stop").addEventListener("click",()=>speechSynthesis.cancel()),document.querySelectorAll("input[type=radio]").forEach(e=>e.addEventListener("click",(function(){langSelect(this)}))),document.querySelector(".tts-input").addEventListener("keyup",(function(){updateLengthValue(),lengthElemHider()})),document.querySelector(".shorten").addEventListener("click",(function(){document.querySelector("textarea").value=document.querySelector("textarea").value.slice(0,3999),updateLengthValue(),lengthElemHider()})),updateLengthValue();